begin
wwv_flow_api.create_install_script(
 p_id=>wwv_flow_api.id(213663643052973623)
,p_install_id=>wwv_flow_api.id(213663206824966509)
,p_name=>'Supporting Backend'
,p_sequence=>10
,p_script_type=>'INSTALL'
,p_script_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/*',
'drop sequence AOP_SAMPLE_CUST_SEQ;',
'drop sequence AOP_SAMPLE_ORD_SEQ;',
'drop sequence AOP_SAMPLE_PROD_SEQ;',
'drop sequence AOP_SAMPLE_ORDER_ITEMS_SEQ;',
'',
'drop table AOP_SAMPLE_ORDER_ITEMS;',
'drop table AOP_SAMPLE_ORDERS;',
'drop table AOP_SAMPLE_CUSTOMERS;',
'drop table AOP_SAMPLE_STATES;',
'drop table AOP_SAMPLE_PRODUCT_INFO;',
'',
'drop package aop_sample_data_pkg;',
'*/',
'',
'create sequence AOP_SAMPLE_CUST_SEQ start with 100;',
'create sequence AOP_SAMPLE_ORD_SEQ start with 11 increment by 1;',
'create sequence AOP_SAMPLE_PROD_SEQ start with 100;',
'create sequence AOP_SAMPLE_ORDER_ITEMS_SEQ start with 100;',
'',
'-- Table: AOP_SAMPLE_CUSTOMERS',
'CREATE TABLE  "AOP_SAMPLE_CUSTOMERS" (    ',
'    "CUSTOMER_ID"          NUMBER       NOT NULL ENABLE,',
'    "CUST_FIRST_NAME"      VARCHAR2(20) NOT NULL ENABLE,',
'    "CUST_LAST_NAME"       VARCHAR2(20) NOT NULL ENABLE,',
'    "CUST_STREET_ADDRESS1" VARCHAR2(60),',
'    "CUST_STREET_ADDRESS2" VARCHAR2(60),',
'    "CUST_CITY"            VARCHAR2(30),',
'    "CUST_STATE"           VARCHAR2(2),',
'    "CUST_POSTAL_CODE"     VARCHAR2(10),',
'    "CUST_EMAIL"           VARCHAR2(30),',
'    "PHONE_NUMBER1"        VARCHAR2(25),',
'    "PHONE_NUMBER2"        VARCHAR2(25),',
'    "URL"                  VARCHAR2(100),',
'    "CREDIT_LIMIT"         NUMBER(9,2),',
'    "TAGS"                 VARCHAR2(4000),',
'    CONSTRAINT "AOP_SAMPLE_CUST_CREDIT_LIMIT_MAX" CHECK (credit_limit <= 5000) ENABLE,',
'    CONSTRAINT "AOP_SAMPLE_CUSTOMERS_PK" PRIMARY KEY ("CUSTOMER_ID") ENABLE,',
'    CONSTRAINT "AOP_SAMPLE_CUSTOMERS_UK" UNIQUE ("CUST_FIRST_NAME","CUST_LAST_NAME")',
' );',
'CREATE INDEX  "AOP_SAMPLE_CUST_NAME_IX" ON  "AOP_SAMPLE_CUSTOMERS" ("CUST_LAST_NAME", "CUST_FIRST_NAME");',
'',
'CREATE OR REPLACE TRIGGER  "AOP_SAMPLE_CUSTOMERS_BIU"',
'  before insert or update ON AOP_SAMPLE_customers FOR EACH ROW',
'DECLARE',
'  cust_id number;',
'BEGIN',
'  if inserting then  ',
'    if :new.customer_id is null then',
'      select AOP_SAMPLE_cust_seq.nextval',
'        into cust_id',
'        from dual;',
'      :new.customer_id := cust_id;',
'    end if;',
'  end if;',
'END;',
'/',
'',
'',
'-- Table: AOP_SAMPLE_ORDERS',
'CREATE TABLE  "AOP_SAMPLE_ORDERS" (    ',
'    "ORDER_ID"           NUMBER NOT NULL ENABLE,',
'    "CUSTOMER_ID"        NUMBER NOT NULL ENABLE,',
'    "ORDER_TOTAL"        NUMBER(8,2),',
'    "ORDER_TIMESTAMP"    TIMESTAMP with local time zone,',
'    "USER_NAME"          VARCHAR2(100),',
'    "TAGS"               VARCHAR2(4000),',
'    CONSTRAINT "AOP_SAMPLE_ORDER_TOTAL_MIN" CHECK (order_total >= 0) ENABLE,',
'    CONSTRAINT "AOP_SAMPLE_ORDER_PK" PRIMARY KEY ("ORDER_ID") ENABLE,',
'    CONSTRAINT "AOP_SAMPLE_ORDERS_CUSTOMER_ID_FK" FOREIGN KEY ("CUSTOMER_ID")',
'    REFERENCES  "AOP_SAMPLE_CUSTOMERS" ("CUSTOMER_ID") ON DELETE CASCADE ENABLE',
');',
'CREATE INDEX  "AOP_SAMPLE_ORD_CUSTOMER_IX" ON  "AOP_SAMPLE_ORDERS" ("CUSTOMER_ID");',
'',
'CREATE OR REPLACE TRIGGER  "AOP_SAMPLE_ORDERS_BIU"',
'  before insert or update ON AOP_SAMPLE_orders FOR EACH ROW',
'DECLARE',
'  order_id number;',
'BEGIN',
'  if inserting then  ',
'    if :new.order_id is null then',
'      select AOP_SAMPLE_ord_seq.nextval',
'        INTO order_id',
'        FROM dual;',
'      :new.order_id := order_id;',
'    end if;',
'  end if;  ',
'END;',
'/',
'',
'-- Table: AOP_SAMPLE_PRODUCT_INFO',
'CREATE TABLE  "AOP_SAMPLE_PRODUCT_INFO" (    ',
'    "PRODUCT_ID"          NUMBER NOT NULL ENABLE,',
'    "PRODUCT_NAME"        VARCHAR2(50),',
'    "PRODUCT_DESCRIPTION" VARCHAR2(2000),',
'    "CATEGORY"            VARCHAR2(30),',
'    "PRODUCT_AVAIL"       VARCHAR2(1),',
'    "LIST_PRICE"          NUMBER(8,2),',
'    "PRODUCT_IMAGE"       BLOB,',
'    "MIMETYPE"            VARCHAR2(255),',
'    "FILENAME"            VARCHAR2(400),',
'    "IMAGE_LAST_UPDATE"   TIMESTAMP with local time zone,',
'    "TAGS"                VARCHAR2(4000),',
'    CONSTRAINT "AOP_SAMPLE_PRODUCT_INFO_PK" primary key ("PRODUCT_ID") ENABLE,',
'    CONSTRAINT "AOP_SAMPLE_PRODUCT_INFO_UK" unique ("PRODUCT_NAME") ENABLE',
');',
'',
'CREATE OR REPLACE TRIGGER  "AOP_SAMPLE_PRODUCT_INFO_BIU"',
'  before insert or update ON AOP_SAMPLE_product_info FOR EACH ROW',
'DECLARE',
'  prod_id number;',
'BEGIN',
'  if inserting then  ',
'    if :new.product_id is null then',
'      select AOP_SAMPLE_prod_seq.nextval',
'        into prod_id',
'        from dual;',
'      :new.product_id := prod_id;',
'    end if;',
'  end if;',
'END;',
'/',
'',
'',
'-- Table:  AOP_SAMPLE_ORDER_ITEMS',
'CREATE TABLE  "AOP_SAMPLE_ORDER_ITEMS" (',
'    "ORDER_ITEM_ID" NUMBER(3,0) NOT NULL ENABLE,',
