'CREATE OR REPLACE TRIGGER  "AOP_SAMPLE_ORDER_ITEMS_AIUD_TOTAL"',
'  after insert or update or delete on AOP_SAMPLE_order_items',
'begin',
'  -- Update the Order Total when any order item is changed',
'  update AOP_SAMPLE_orders set order_total =',
'  (select sum(unit_price*quantity) from AOP_SAMPLE_order_items',
'    where AOP_SAMPLE_order_items.order_id = AOP_SAMPLE_orders.order_id);',
'end;',
