'CREATE OR REPLACE TRIGGER  "AOP_SAMPLE_ORDER_ITEMS_BIU_GET_PRICE"',
'  before insert or update on AOP_SAMPLE_order_items for each row',
'declare',
'  l_list_price number;',
'begin',
'  if :new.unit_price is null then',
'    -- First, we need to get the current list price of the order line item',
'    select list_price',
'    into l_list_price',
'    from AOP_SAMPLE_product_info',
'    where product_id = :new.product_id;',
'    -- Once we have the correct price, we will update the order line with the correct price',
'    :new.unit_price := l_list_price;',
'  end if;',
'end;',
'/',
